                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      class swap{
	public static void main (String [] arg){

	int a=10;
	int b=15;
	System.out.print("before swapping values of a ="+a+"and b="+b);
	int temp;
	temp = a;
	a=b;
	b=temp;
	System.out.println("swapped values of a ="+a+"and b= "+b);
	
  }
}                                                                                                                                                                                                       